import {
  Box,
  Grid,
  Paper,
  Typography,
  Card,
  CardContent,
  CardActions,
  Button,
  Chip,
  LinearProgress,
  List,
  ListItem,
  ListItemText,
  Avatar,
} from '@mui/material';
import {
  ShoppingBag as ShopifyIcon,
  Email as GmailIcon,
  Payment as StripeIcon,
  Message as SlackIcon,
  CheckCircle as CheckCircleIcon,
  Warning as WarningIcon,
  TrendingUp as TrendingUpIcon,
  Speed as SpeedIcon,
} from '@mui/icons-material';
import { useQuery } from '@tanstack/react-query';
// import { getDetectedServices } from '../../services/api';

// „Çµ„Éº„Éì„Çπ„Ç¢„Ç§„Ç≥„É≥„Éû„ÉÉ„Éî„É≥„Ç∞
// const serviceIcons: Record<string, React.ReactNode> = {
//   shopify: <ShopifyIcon />,
//   gmail: <GmailIcon />,
//   stripe: <StripeIcon />,
//   slack: <SlackIcon />,
// };

// „É¢„ÉÉ„ÇØ„Éá„Éº„ÇøÔºàÂÆüÈöõ„ÅØAPI„Åã„ÇâÂèñÂæóÔºâ
const mockServices = [
  {
    id: 'shopify-1',
    name: 'Shopify',
    icon: 'üõçÔ∏è',
    status: 'connected',
    isLoggedIn: true,
    lastSync: '5ÂàÜÂâç',
    stats: {
      orders: 156,
      products: 423,
      customers: 1289,
    },
  },
  {
    id: 'gmail-1',
    name: 'Gmail',
    icon: 'üìß',
    status: 'connected',
    isLoggedIn: true,
    lastSync: '1ÂàÜÂâç',
    stats: {
      unread: 12,
      total: 3456,
      sent: 234,
    },
  },
  {
    id: 'stripe-1',
    name: 'Stripe',
    icon: 'üí≥',
    status: 'connected',
    isLoggedIn: true,
    lastSync: '10ÂàÜÂâç',
    stats: {
      revenue: '¬•1,234,567',
      transactions: 89,
      subscriptions: 45,
    },
  },
  {
    id: 'slack-1',
    name: 'Slack',
    icon: 'üí¨',
    status: 'disconnected',
    isLoggedIn: false,
    lastSync: null,
    stats: null,
  },
];

const Dashboard: React.FC = () => {
  // ÂÆüÈöõ„ÅÆAPI„Ç≥„Éº„É´ÔºàÁèæÂú®„ÅØ„É¢„ÉÉ„ÇØ„Éá„Éº„Çø„Çí‰ΩøÁî®Ôºâ
  const { data: services = mockServices, isLoading } = useQuery({
    queryKey: ['services'],
    queryFn: async () => {
      // return await getDetectedServices();
      return mockServices; // ÈñãÁô∫‰∏≠„ÅØ„É¢„ÉÉ„ÇØ„Éá„Éº„Çø„Çí‰ΩøÁî®
    },
    refetchInterval: 30000, // 30Áßí„Åî„Å®„Å´Êõ¥Êñ∞
  });

  const connectedServices = services.filter(s => s.status === 'connected').length;
  const totalServices = services.length;

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        „ÉÄ„ÉÉ„Ç∑„É•„Éú„Éº„Éâ
      </Typography>

      {/* Áµ±Ë®à„Ç´„Éº„Éâ */}
      <Grid container spacing={3} sx={{ mb: 4 }}>
        <Grid item xs={12} sm={6} md={3}>
          <Paper sx={{ p: 2 }}>
            <Box sx={{ display: 'flex', alignItems: 'center', mb: 1 }}>
              <CheckCircleIcon color="success" sx={{ mr: 1 }} />
              <Typography variant="subtitle2" color="text.secondary">
                Êé•Á∂öÊ∏à„Åø„Çµ„Éº„Éì„Çπ
              </Typography>
            </Box>
            <Typography variant="h4">
              {connectedServices}/{totalServices}
            </Typography>
            <LinearProgress
              variant="determinate"
